#include <stdio.h>
#include <stdlib.h>

#include "StaticFiles.h"

char *connectHTML;
char *connectJS;

char *StaticFiles::readFile(const char *filename)
{
  char *buffer;
  long length;
  FILE *f = fopen(filename, "r");
  if (f)
  {
    fseek(f, 0, SEEK_END);
    length = ftell(f);
    fseek(f, 0, SEEK_SET);
    buffer = (char *)malloc(length);
    if (buffer)
    {
      fread(buffer, 1, length, f);
    }
    fclose(f);
  }

  return buffer;
}

